import 'dart:convert';import 'package:finaltask_eudeka/models/drink_name.dart';import 'package:finaltask_eudeka/models/drink_detail.dart';import 'package:http/http.dart';class ApiProvider {  String baseUrl = 'https://www.thecocktaildb.com/api/json/v1/1';  Client client = Client();  Future<DrinkName> getDrinkName() async {    Response response = await client.get('$baseUrl/filter.php?c=Cocktail');    if (response.statusCode == 200) {      return DrinkName.fromJson(jsonDecode(response.body));    } else {      throw Exception(response.statusCode);    }  }  Future<DrinkDetail> getDrinkDetail(String id) async {    Response response =    await client.get('$baseUrl/lookup.php?i=$id');    print(response.body);    print('$baseUrl/lookup.php?i=$id');    if (response.statusCode == 200) {      return DrinkDetail.fromJson(jsonDecode(response.body));    } else {      throw Exception(response.statusCode);    }  }  Future<DrinkName> getDrinkSearch(String name) async {    Response response =    await client.get('$baseUrl/search.php?s=$name');    if (response.statusCode == 200) {      return DrinkName.fromJson(jsonDecode(response.body));    } else {      throw Exception(response.statusCode);    }  }}