import 'package:flutter/material.dart';class Login extends StatefulWidget {  @override  _LoginState createState() => _LoginState();}class _LoginState extends State<Login> {  @override  Widget build(BuildContext context) {    return Scaffold(      body: Container(        color: Colors.red,        padding: EdgeInsets.all(20),        child: ListView(          children: <Widget>[            Center(              child: Column(                children: <Widget>[                  _iconLogin(),                  Text(                    'LOGIN',                    style: TextStyle(                        fontWeight: FontWeight.bold,                        fontSize: 30,                        color: Colors.white),                  ),                  _textFieldLogin(),                  _buildButton(context)                ],              ),            )          ],        ),      ),    );  }}Widget _iconLogin() {  return Container(      margin: EdgeInsets.only(top: 25, bottom: 20),      child: Image.asset(        'assets/images/drinks-white.png',        width: 150,        height: 150,      ));}Widget _textFieldLogin() {  return Column(    children: <Widget>[      Padding(        padding: EdgeInsets.only(top: 12.0),      ),      TextFormField(        decoration: const InputDecoration(          border: UnderlineInputBorder(),          enabledBorder: UnderlineInputBorder(            borderSide: BorderSide(              color: Colors.white,              width: 1.5,            ),          ),          focusedBorder: UnderlineInputBorder(            borderSide: BorderSide(              color: Colors.white,              width: 3.0,            ),          ),          hintText: "Username",          hintStyle: TextStyle(color: Colors.white),        ),        style: TextStyle(color: Colors.white),        autofocus: false,      ),      Padding(        padding: EdgeInsets.only(top: 12.0),      ),      TextFormField(        decoration: const InputDecoration(          border: UnderlineInputBorder(),          enabledBorder: UnderlineInputBorder(            borderSide: BorderSide(              color: Colors.white,              width: 1.5,            ),          ),          focusedBorder: UnderlineInputBorder(            borderSide: BorderSide(              color: Colors.white,              width: 3.0,            ),          ),          hintText: "Password",          hintStyle: TextStyle(color: Colors.white),        ),        style: TextStyle(color: Colors.white),        obscureText: true,        autofocus: false,      ),    ],  );}Widget _buildButton(BuildContext context) {  return Column(    children: <Widget>[      Padding(        padding: EdgeInsets.only(top: 16.0),      ),      InkWell(        child: Container(          padding: EdgeInsets.symmetric(vertical: 8.0),          width: double.infinity,          child: Text(            'Login',            style: TextStyle(color: Colors.red),            textAlign: TextAlign.center,          ),          decoration: BoxDecoration(            color: Colors.white,            borderRadius: BorderRadius.circular(20.0),          ),        ),      ),      Padding(        padding: EdgeInsets.only(top: 16.0),      ),      Text(        'or',        style: TextStyle(          color: Colors.white,          fontSize: 12.0,        ),      ),      FlatButton(        child: Text(          'Register',          style: TextStyle(color: Colors.white),        ),        onPressed: () {},      ),    ],  );}