import 'package:finaltask_eudeka/pages/home.dart';import 'package:flutter/material.dart';import 'dart:async';class SplashScreen extends StatefulWidget {  @override  _SplashScreenState createState() => _SplashScreenState();}class _SplashScreenState extends State<SplashScreen> {  @override  void initState() {    startSplashScreen();    super.initState();  }  startSplashScreen() async {    var duration = const Duration(seconds: 5);    return Timer(      duration,      () {        Navigator.of(context).pushReplacement(          MaterialPageRoute(            builder: (_) {              return Home();            },          ),        );      },    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.red,      body: Center(        child: Icon(Icons.account_circle, size: 100,),      ),    );  }}